import React, { useEffect, useState } from 'react'
import api from '../services/api'

export default function VehiclePanel({ vehicles = [], selectedLocation, onRefresh, onStartAssign, onCreated }){
  const [list, setList] = useState([])
  const [form, setForm] = useState({ name: '', location: { x: 0, y: 0 } })
  const [editingId, setEditingId] = useState(null)

  useEffect(()=> setList(vehicles), [vehicles])

  // If the user has clicked on the map while assigning, update the form location
  useEffect(()=>{
    if(selectedLocation){
      setForm(f => ({...f, location: selectedLocation }))
    }
  },[selectedLocation])

  const create = async ()=>{
    try{ await api.createVehicle(form); setForm({name:'', location:{x:0,y:0}}); onRefresh && onRefresh(); onCreated && onCreated() }catch(e){ console.error(e) }
  }

  const remove = async (id)=>{ try{ await api.deleteVehicle(id); onRefresh && onRefresh() }catch(e){ console.error(e) } }

  const startEdit = (veh)=> { setEditingId(veh.id); setForm({ name: veh.name, location: veh.location }) }

  const saveEdit = async (id)=>{ try{ await api.updateVehicle(id, form); setEditingId(null); onRefresh && onRefresh() }catch(e){ console.error(e) } }

  return (
    <div className="panel">
      <h3>Vehicles</h3>
      <div style={{marginBottom:8}}>
        <input placeholder="Name" value={form.name} onChange={e=>setForm({...form, name:e.target.value})} />
        <span style={{marginLeft:8}} className="coords-box">x: {form.location.x} y: {form.location.y}</span>
        <button className="btn" onClick={onStartAssign} style={{marginLeft:8}}>Assign from map</button>
        <button className="btn primary" onClick={create} style={{marginLeft:8}}>Create</button>
      </div>

      <div>
        {list.map((veh) => (
          <div key={veh.id} className="item-row">
            {editingId === veh.id ? (
              <>
                <input value={form.name} onChange={e=>setForm({...form, name: e.target.value})} />
                <span className="coords-box">x: {form.location.x} y: {form.location.y}</span>
                <button className="btn" onClick={onStartAssign}>Assign from map</button>
                <button className="btn primary" onClick={()=>saveEdit(veh.id)}>Save</button>
                <button className="btn" onClick={()=>setEditingId(null)}>Cancel</button>
              </>
            ) : (
              <>
                <div style={{flex:1}}>
                  <strong>{veh.name}</strong>
                  <div className="coords-box">x: {veh.location?.x} y: {veh.location?.y}</div>
                </div>
                <button className="btn" onClick={()=>startEdit(veh)}>Edit</button>
                <button className="btn danger" onClick={()=>remove(veh.id)}>Delete</button>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  )
}
