import React, { useEffect, useRef, useState } from 'react'

// MapDisplay shows the map image and overlays incidents (red) and vehicles (blue).
// Coordinates passed in incidents/vehicles are expected to be normalized [0-1].

export default function MapDisplay({ incidents = [], vehicles = [], selectedIncidentLocation, selectedVehicleLocation, onMapClick }){
  const imgRef = useRef(null)
  const svgRef = useRef(null)
  const [displaySize, setDisplaySize] = useState({w: 600, h: 400})

  useEffect(()=>{
    const img = imgRef.current
    if(!img) return
    const update = ()=>{
      const rect = img.getBoundingClientRect()
      setDisplaySize({ w: rect.width, h: rect.height })
    }
    update()
    const ro = new ResizeObserver(update)
    ro.observe(img)
    window.addEventListener('resize', update)
    return ()=>{ ro.disconnect(); window.removeEventListener('resize', update) }
  },[])

  const handleClick = (e) =>{
    const img = imgRef.current
    if(!img) return
    const rect = img.getBoundingClientRect()
    // Use client coordinates relative to image rect for robustness
    let x = (e.clientX - rect.left) / rect.width
    let y = (e.clientY - rect.top) / rect.height
    // clamp
    x = Math.max(0, Math.min(1, x))
    y = Math.max(0, Math.min(1, y))
    onMapClick && onMapClick({ x: parseFloat(x.toFixed(4)), y: parseFloat(y.toFixed(4)) })
  }

  const renderMarkers = () => {
    const elems = []
    const r = 8
    const w = displaySize.w
    const h = displaySize.h

    // Only show pending incidents on the map
    incidents.filter(i => i.status === 'pending').forEach((inc) => {
      const x = (inc.location?.x ?? 0) * w
      const y = (inc.location?.y ?? 0) * h
      elems.push(
        <g key={`inc-${inc.id}`}> 
          <circle cx={x} cy={y} r={r} fill="red" stroke="#900" strokeWidth={1} />
          <text x={x + r + 6} y={y - r/2} fontSize={12} fill="#6b1222" style={{fontWeight:600}}>{inc.name}</text>
        </g>
      )
    })
    vehicles.forEach((veh) => {
      const x = (veh.location?.x ?? 0) * w
      const y = (veh.location?.y ?? 0) * h
      elems.push(
        <g key={`veh-${veh.id}`}>
          <circle cx={x} cy={y} r={r} fill="blue" stroke="#003" strokeWidth={1} />
          <text x={x + r + 6} y={y - r/2} fontSize={12} fill="#022a55" style={{fontWeight:600}}>{veh.name}</text>
        </g>
      )
    })

    // Selected (to-be-created or edited) markers appear as green dots
    if(selectedIncidentLocation){
      const x = (selectedIncidentLocation.x ?? 0) * w
      const y = (selectedIncidentLocation.y ?? 0) * h
      elems.push(<circle key="selected-incident" cx={x} cy={y} r={6} fill="limegreen" stroke="#065" strokeWidth={1} />)
    }
    if(selectedVehicleLocation){
      const x = (selectedVehicleLocation.x ?? 0) * w
      const y = (selectedVehicleLocation.y ?? 0) * h
      elems.push(<circle key="selected-vehicle" cx={x} cy={y} r={6} fill="#00cc66" stroke="#036" strokeWidth={1} />)
    }

    return elems
  }

  return (
    <div className="map-container panel">
      <div style={{textAlign:'center', paddingBottom:6}} className="coords-box">Click map to pick location</div>
      <img
        ref={imgRef}
        className="map-image"
        src="/map.png"
        alt="map"
        onClick={handleClick}
      />

      <svg
        ref={svgRef}
        className="overlay-svg"
        style={{ position:'absolute', left:0, top:0, width:'100%', height:'100%' }}
        viewBox={`0 0 ${displaySize.w} ${displaySize.h}`}
        preserveAspectRatio="none"
      >
        {renderMarkers()}
      </svg>
    </div>
  )
}
