import React, { useEffect, useState } from 'react'
import api from '../services/api'

export default function IncidentPanel({ incidents = [], selectedLocation, onRefresh }){
  const [list, setList] = useState([])
  const [form, setForm] = useState({ name: '', status: 'pending', location: { x: 0, y: 0 } })
  const [editingId, setEditingId] = useState(null)

  useEffect(()=> setList(incidents), [incidents])

  useEffect(()=>{
    if(selectedLocation){
      setForm(f => ({...f, location: selectedLocation }))
    }
  },[selectedLocation])

  const create = async ()=>{
    try{
      await api.createIncident(form)
      setForm({ name:'', status:'pending', location:{x:0,y:0} })
      onRefresh && onRefresh()
    }catch(e){ console.error(e) }
  }

  const remove = async (id)=>{
    try{ await api.deleteIncident(id); onRefresh && onRefresh() }catch(e){ console.error(e) }
  }

  const startEdit = (inc)=> setEditingId(inc.id) || setForm({ name: inc.name, status: inc.status, location: inc.location })

  const saveEdit = async (id)=>{
    try{ await api.updateIncident(id, form); setEditingId(null); onRefresh && onRefresh() }catch(e){ console.error(e) }
  }

  return (
    <div className="panel">
      <h3>Incidents</h3>

      <div style={{marginBottom:8}}>
        <input placeholder="Name" value={form.name} onChange={e=>setForm({...form, name:e.target.value})} />
        <select value={form.status} onChange={e=>setForm({...form, status:e.target.value})}>
          <option value="pending">pending</option>
          <option value="resolved">resolved</option>
        </select>
        <span style={{marginLeft:8}} className="coords-box">x: {form.location.x} y: {form.location.y}</span>
        <button className="btn primary" onClick={create} style={{marginLeft:8}}>Create</button>
      </div>

      <div>
        {list.map((inc) => (
          <div key={inc.id} className="item-row">
            {editingId === inc.id ? (
              <>
                <input value={form.name} onChange={e=>setForm({...form, name: e.target.value})} />
                <select value={form.status} onChange={e=>setForm({...form, status:e.target.value})}>
                  <option value="pending">pending</option>
                  <option value="resolved">resolved</option>
                </select>
                <button className="btn primary" onClick={()=>saveEdit(inc.id)}>Save</button>
                <button className="btn" onClick={()=>setEditingId(null)}>Cancel</button>
              </>
            ) : (
              <>
                <div style={{flex:1}}>
                  <strong>{inc.name}</strong> <span style={{color:'#666'}}>[{inc.status}]</span>
                  <div className="coords-box">x: {inc.location?.x} y: {inc.location?.y}</div>
                </div>
                <button className="btn" onClick={()=>{ setForm({ name: inc.name, status: inc.status, location: inc.location }); setEditingId(inc.id); }}>Edit</button>
                <button className="btn danger" onClick={()=>remove(inc.id)}>Delete</button>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  )
}
