import api from '../services/api'

export default function WindDisplay(){
  const [wind, setWind] = useState(null)

  useEffect(()=>{
    const load = async ()=>{
      try{ const res = await api.getWindInfo(); setWind(res.data) }catch(e){ console.error(e) }
    }
    load()
  },[])

